</style><script>
function setStyle(selector, propName, propVal) { [].forEach.call(document.querySelectorAll(selector), function (el) { el.style[propName] = propVal; }); }
function currentDeckMessages() {

var article = document.getElementsByTagName("article")[0];
setStyle('.subdeck', 'display', 'none');
var subdeck = article.dataset.deck.replace(/^General(?:::Language)?::((?:(?!::).)*)(?:::.*)?/, '$1');
if(subdeck === ''){ setStyle('.subdeck.Other', 'display', ''); }
else { setStyle('.subdeck.' + subdeck, 'display', ''); }

var title = document.getElementById("title");
if ( title.innerText.length > 50 ){
title.style.fontSize = '80%';
}
if ( title.innerText.length > 30 ){
title.style.fontWeight = '500';
title.style.letterSpacing = '-4px';
}

[].forEach.call(document.querySelectorAll('blockquote'), function (el) {
var fontSize = Math.round( 300 / el.innerText.length * 100 );
if (fontSize > 70) { fontSize = 70; }
if (fontSize < 30) { fontSize = 30; }
if (el.parentElement.nodeName == "DETAILS"){ fontSize = fontSize * 3; }
el.style['fontSize'] = fontSize + '%';
//title.innerText = document.defaultView.getComputedStyle(el).getPropertyValue('font-size');
});

article.insertAdjacentHTML('beforeend', '<p style="font-size:15px; text-align:center; opacity: 0.5;">'+subdeck+"</p>");

}
function playSound() {
    var a = new Audio(document.getElementById("audio"));
    a.play();
}
</script><style>
