tmpls.qfmt:
{{#description}}
  {{#command only}}
    <div class="question">Command that:</div>
    <div class="definition long">{{description}}</div>
  {{/command only}}
{{/description}}

{{#description}}
  {{#short option}}
    <span class="question">Short option to make <b>{{text:command}}</b></span>
    <div class="definition long">{{description}}</div>
  {{/short option}}
{{/description}}

{{#description}}
  {{#long option}}
    <span class="question">Long option to make <b>{{text:command}}</b></span>
    <div class="definition long">{{description}}</div>
  {{/long option}}
{{/description}}

{{#long option}}
  {{#short option}}
    <div class="question">short form of</div>
    {{command}} {{long option}}
  {{/short option}}
{{/long option}}

{{#long option}}
  {{#short option}}
    <div class="question">long form of</div>
    {{command}} {{short option}}
  {{/short option}}
{{/long option}}

{{#description}}
  {{#extra}}
    <div class="question">
      <b>{{command}}</b>
      {{text:extra type}} that means
    </div>
    <div class="definition long">{{description}}</div>
  {{/extra}}
{{/description}}

